  const getTimeToMInt = async () => {
    const date = new Date();
    const time: any = parseInt((date.getTime() / 1000).toFixed(0));
    let k = date.getTimezoneOffset();
    if (time >= PUBLIC_TIME) {
      setCurrentWl("PUBLIC");
      currentWltype = "PUBLIC";
    } else if (time >= WL_TIME) {
      setCurrentWl("WL");
      currentWltype = "WL";
    } else if (time >= GOG_TIME) {
      setCurrentWl("GOG + OG");
      currentWltype = "GOG + OG";
    }
    if (currentWltype == "PUBLIC") {
      return "";
    } else {
      if (currentWltype == "GOG + OG") {
        const date = new Date();
        const time: any = parseInt((date.getTime() / 1000).toFixed(0));
        // if (WL_TIME >= time) {
        //   setCurrentWl('WL');
        //   return;
        // }
        var delta = Math.abs(time - WL_TIME);
        if (delta <= 0) {
          setCurrentWl("WL");
          currentWltype = "WL";
        }
        let days: any = Math.floor(delta / 86400);
        delta -= days * 86400;
        let hours: any = Math.floor(delta / 3600) % 24;
        delta -= hours * 3600;
        let minutes: any = Math.floor(delta / 60) % 60;
        delta -= minutes * 60;
        let seconds: any = delta % 60;
        // let hours:any = parseInt((((time) - WL_TIME) / 3600).toFixed(0));
        // let minutes:any = parseInt(((((time) - WL_TIME) % 3600) / 60).toFixed(0));
        // let seconds:any = parseInt((((time) - WL_TIME) % 60).toFixed(0));
        hours = Math.abs(hours);
        if (days < 10) {
          days = "0" + days;
        }
        if (hours < 10) {
          hours = "0" + hours;
        }
        minutes = Math.abs(minutes);
        if (minutes < 10) {
          minutes = "0" + minutes;
        }
        seconds = Math.abs(seconds);
        if (seconds < 10) {
          seconds = "0" + seconds;
        }
        setTime(hours + ":" + minutes + ":" + seconds);
      } else if (currentWltype == "WL") {
        const date = new Date();
        const time: any = parseInt((date.getTime() / 1000).toFixed(0));
        var delta = Math.abs(time - PUBLIC_TIME);
        if (delta <= 0) {
          setCurrentWl("PUBLIC");
          currentWltype = "PUBLIC";
        }
        let days: any = Math.floor(delta / 86400);
        delta -= days * 86400;
        let hours: any = Math.floor(delta / 3600) % 24;
        delta -= hours * 3600;
        let minutes: any = Math.floor(delta / 60) % 60;
        delta -= minutes * 60;
        let seconds: any = delta % 60;
        hours = Math.abs(hours);
        if (days < 10) {
          days = "0" + days;
        }
        if (hours < 10) {
          hours = "0" + hours;
        }
        minutes = Math.abs(minutes);
        if (minutes < 10) {
          minutes = "0" + minutes;
        }
        seconds = Math.abs(seconds);
        if (seconds < 10) {
          seconds = "0" + seconds;
        }
        setTime(hours + ":" + minutes + ":" + seconds);
      } else if (currentWl == "PUBLIC") {
        setTime("");
      } else {
        const date = new Date();
        const time: any = parseInt((date.getTime() / 1000).toFixed(0));
        // if (GOG_TIME > time) {
        //   setCurrentWl('GOG + OG');
        //   return;
        // }
        var delta = Math.abs(time - GOG_TIME);
        if (delta <= 0) {
          setCurrentWl("GOG + OG");
          currentWltype = "GOG + OG";
        }
        let days: any = Math.floor(delta / 86400);
        delta -= days * 86400;
        let hours: any = Math.floor(delta / 3600) % 24;
        delta -= hours * 3600;
        let minutes: any = Math.floor(delta / 60) % 60;
        delta -= minutes * 60;
        let seconds: any = delta % 60;
        // let hours:any = parseInt((((time) - WL_TIME) / 3600).toFixed(0));
        // let minutes:any = parseInt(((((time) - WL_TIME) % 3600) / 60).toFixed(0));
        // let seconds:any = parseInt((((time) - WL_TIME) % 60).toFixed(0));
        hours = Math.abs(hours);
        if (days < 10) {
          days = "0" + days;
        }
        if (hours < 10) {
          hours = "0" + hours;
        }
        minutes = Math.abs(minutes);
        if (minutes < 10) {
          minutes = "0" + minutes;
        }
        seconds = Math.abs(seconds);
        if (seconds < 10) {
          seconds = "0" + seconds;
        }
        setTime(hours + ":" + minutes + ":" + seconds);
      }
    }
