    let k = date.getTimezoneOffset();
    if ((time + k) > 1656262545) {
      setCurrentWl('GOG + OG');
    }
    else if ((time + k)  > 1656262725) {
      setCurrentWl('WL');
    }
    else if ((time + k)  > 1656262845) {
      setCurrentWl('PUBLIC');
    }
    if (currentWl == 'GOG + OG') {
      const date = new Date();
      const time:any = parseInt((date.getTime()/1000).toFixed(0));;
      let k = date.getTimezoneOffset();
      var hours = (Math.abs((time + k) - WL_TIME) / 3600).toFixed(0);
      var minutes = ((((time + k) - WL_TIME) % 3600) / 60).toFixed(0);
      var seconds = (Math.abs((time + k) - WL_TIME) % 60).toFixed(0);
      setTime(hours  + ':' +  minutes + ':' + seconds);
    }
    else if (currentWl == 'WL') {
      const date = new Date();
      const time:any = parseInt((date.getTime()/1000).toFixed(0));;
      var hours = (Math.abs((time + k) - PUBLIC_TIME) / 3600).toFixed(0);
      var minutes = ((((time + k) - PUBLIC_TIME) % 3600) / 60).toFixed(0);
      var seconds = (Math.abs((time + k) - PUBLIC_TIME) % 60).toFixed(0);
      setTime(hours  + ':' +  minutes + ':' + seconds);
    }
    else if (currentWl == 'PUBLIC') {
      setTime('');
    }
    else {
      const date = new Date();
      const time:any = parseInt((date.getTime()/1000).toFixed(0));;
      let hours:number = parseInt((((time + k) - GOG_TIME) / 3600).toFixed(0));
      let minutes:number = parseInt(((((time + k) - GOG_TIME) % 3600) / 60).toFixed(0));
      let seconds:number = parseInt((((time + k) - GOG_TIME) % 60).toFixed(0));
      if (hours < 0) {
        hours = 0
      }
      else if (minutes < 0) {
        minutes = 0
      } else if (seconds < 0) {
        seconds = 0
      }
      setTime(hours  + ':' +  minutes + ':' + seconds);
    }
  }

